add_css:
body {
  line-height: 1.6;
}

.page-header {
  background-color: #61A220;
  border-bottom: none;

  -moz-box-shadow: inset 0 0 15px -5px rgba(0, 0, 0, 0.7);
  -webkit-box-shadow: inset 0 0 15px -5px rgba(0, 0, 0, 0.7);
  box-shadow: inset 0 0 15px -5px rgba(0, 0, 0, 0.7);
}

.page-header * {
  color: #fff !important;
  text-shadow: 0 0 30px rgba(0, 0, 0, 0.3);
}

.navbar {
  padding: 10px 0px;
  border-bottom: none;
}

.web-footer {
  border-top: none;
}

.sidebar-item a:hover {
  color: #333;
  border-bottom: 2px solid #F28300;
}

.page-container a {
  text-decoration: none !important;
}

.page-container a:hover {
  border-bottom: 2px solid #F28300;
}

.leader-image {
  width: 150px;
  height: 150px;
  background-color: #eee;
  background-size: cover;
  background-position: center center;
  border-radius: 4px;
  float: left;
}

.leaders-social-icons {
  font-size: 150%;
}

.leader-image-container {
  margin-right: 20px;
  margin-bottom: 15px;
}

.leader-image-container p {
  margin-top: 15px;
  margin-bottom: 0px
}
